using System;
using System.Collections.Generic;

public class MiniGameEngine
{
    private Random rand = new Random();
    private Dictionary<string, int> playerStats = new Dictionary<string, int>();

    public MiniGameEngine()
    {
        playerStats["HP"] = 100;
        playerStats["MP"] = 50;
        playerStats["ATK"] = 12;
        playerStats["DEF"] = 6;
    }

    public void StartGame()
    {
        Console.WriteLine("=== MINI GAME STARTED ===");
        Console.WriteLine("Player HP: " + playerStats["HP"]);
        Console.WriteLine("Player MP: " + playerStats["MP"]);
    }

    public void AttackEnemy(string enemy)
    {
        int damage = rand.Next(5, 20);
        Console.WriteLine($"You attack {enemy} and deal {damage} damage!");
    }

    public void TakeDamage(int dmg)
    {
        int reduced = Math.Max(0, dmg - playerStats["DEF"]);
        playerStats["HP"] -= reduced;
        Console.WriteLine($"You received {reduced} damage! HP now: {playerStats["HP"]}");
    }

    public void CastSpell(string name)
    {
        if (playerStats["MP"] < 5)
        {
            Console.WriteLine("Not enough MP!");
            return;
        }

        playerStats["MP"] -= 5;
        Console.WriteLine($"You cast spell '{name}'! MP now: {playerStats["MP"]}");
    }

    public void Heal()
    {
        int heal = rand.Next(10, 25);
        playerStats["HP"] += heal;
        Console.WriteLine($"You healed {heal} HP! HP now: {playerStats["HP"]}");
    }

    public void RandomEvent()
    {
        int r = rand.Next(1, 6);
